import './index.scss';
import React  from 'react';

const questions = [
  {
    title: ' What is React ?',
    variants: ['library', 'framework', 'app'],
    correct: 0,
  },
  {
    title: 'What is a component',
    variants: ['app', 'part of app or page', 'something i never heard about'],
    correct: 1,
  },
  {
    title: 'what is  JSX?',
    variants: [
      `It's a plain HTML`,
      'This is a function',
      ' This is a HTML but with ability to run JS code',
    ],
    correct: 2,
  },
];



function Result() {
  return (
    <div className="result">
      <img src="https://cdn-icons-png.flaticon.com/512/2278/2278992.png" />
      <h2>You guessed 3 out of 10</h2>
      <button>Try again </button>
    </div>
  );
}

function Game({ step, question, onClickVariant }) {

  const percentage = Math.round(step / question.length * 100);

  return (
    <>
      <div className="progress">
        <div style={{ width: `${percentage}%` }} className="progress__inner"></div>
      </div>
      <h1>{question.title}</h1>
      <ul >
        {question.variants.map((x, index) => (<li onClick={()=>onClickVariant(index)} key={x}>{x}</li>))}
      </ul>
    </>
  );
}

function App() {
  const [step, setStep] = React.useState(0)
  const question = questions[step];

  const onClickVariant = (index) => {
    console.log(step, index)
    setStep(step+1)
  }

  return (
    <div className="App">
      <Game step={step} question={question} onClickVariant={onClickVariant} />
      {/* <Result /> */}
    </div>
  );
}

export default App;
